package ArraysInJava;

public class ArraysDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] marks = new int[5];
		System.out.println(marks);
		System.out.println(marks[0]);
		
		marks[1]=10;
		
		for(int i=0; i<=4; i++)
			System.out.print(marks[i]);
		
	}

}
