package FundamentalsOfJava;

public class ComparisonOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 20;
		
		System.out.println(a == b);
		System.out.println(a != b);
		System.out.println(a > b);
		System.out.println(a < b);
		
		int c = 30;
		int d = 30;
		
		System.out.println(c >= d);
		System.out.println(c <= b);
	}

}
