package FundamentalsOfJava;

public class SimpleInterest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double SI = 0;
		double p = 20.5;
		double r = 5.5;
		double t = 2.5;
		
		SI = (p*r*t) / 100;
		
		System.out.println("The Simple Interest is " + SI + " Rs");
		
	}

}
